function SvgIcon({
  title,
  className,
  size: _size,
  icon24,
  icon40,
}: {
  title: string;
  size?: number;
  className?: string;
  icon24?: ReactElement;
  icon40?: ReactElement;
} & ({ icon24: ReactElement } | { icon40: ReactElement })): JSX.Element {
  const size = _size ?? 40;
  const defaultIcon = icon24 ?? icon40;
  assert(defaultIcon);
  const icon = (size < 30 ? icon24 : icon40) ?? defaultIcon;
  return (
    <svg
      aria-hidden="true"
      className={className}
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 -960 960 960"
      width={pxNumbersAsRem(size)}
    >
      <title>{title}</title>
      {icon}
    </svg>
  );
}

